import React from "react";
import './Header.css';
import MenuIcon from "@material-ui/icons/Menu";
import {Avatar, IconButton} from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import AppsIcon from "@material-ui/icons/Apps";
import NotificationsIcon from "@material-ui/icons/Notifications";
import {useDispatch, useSelector} from "react-redux";
import {selectUser} from "./features/userSlice";
import {logout} from "./features/userSlice";
import {auth} from "./Firebase";
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import  {useState, useEffect} from 'react';
import axios from 'axios'; 
import List from './List'; 


const  Header=({handleLogout}) => {
    const user = useSelector(selectUser);
    const dispatch = useDispatch();
 
    const [emails, setemails] = useState([])
    const [search, setSearch] = useState('')

    useEffect(() => {
        const API_URL = 'https://firestore.googleapis.com/v1/projects/my-app-7fd52/databases/(default)/documents/emails?key=AIzaSyBDeWTQfiPCbSigsRNjBp_MAq1AProaadA'
        axios
            .get(API_URL)
            .then(res => {
                const emails = res.data
                setContacts(emails)
            })
    }, [])

    const filteredemails = search.length === 0 ? emails : 
    emails.filter(emails=> emails.message.
                toLowerCase().includes(search.toLowerCase()))




    
    return (
        <div className="header">
          <div className="header_left">
              <IconButton>
              <MenuIcon />
              </IconButton>
              <img src="https://www.freepnglogos.com/uploads/email-logo-png-22.png"  alt="" />
          </div>
          <div className="header_middle "  >
           <SearchIcon />
           <input 
                    type="text" 
                    placeholder="Search name" 
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    />
            <List emails={filteredemails}/>
              </div>  
           <div className="header_right ">
              <IconButton>
                  <AppsIcon />
              </IconButton>

              <IconButton >
                  <NotificationsIcon/>
              </IconButton>
              <Avatar src={user?.photoURL}/>
              <IconButton >    
                  <ExitToAppIcon onClick={handleLogout} />
              </IconButton>
          </div>

        </div>
    );
};

export default Header
