import React from "react";
import './Header.css';
import MenuIcon from "@material-ui/icons/Menu";
import {Avatar, IconButton} from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import AppsIcon from "@material-ui/icons/Apps";
import NotificationsIcon from "@material-ui/icons/Notifications";
import {useDispatch, useSelector} from "react-redux";
import {selectUser} from "./features/userSlice";
import {logout} from "./features/userSlice";
import {auth} from "./Firebase";
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import EmailList from "./EmailList";

const  Header=({handleLogout}) => {
    const user = useSelector(selectUser);
    const dispatch = useDispatch();
 
    const [input, setInput] = useState('');
  const [EmailListDefault, setEmailListDefault] = useState();
  const [EmailList, setEmailList] = useState();

  const fetchData = async () => {
    return await fetch('https://restcountries.eu/rest/v2/all')
      .then(response => response.json())
      .then(data => {
         setEmailList(data) 
         setEmailListDefault(data)
       });}

  const updateInput = async (input) => {
     const filtered = EmailListDefault.filter(email => {
      return email.name.toLowerCase().includes(input.toLowerCase())
     })
     setInput(input);
     setCountryList(filtered);
  }

    return (
        <div className="header">
          <div className="header_left">
              <IconButton>
              <MenuIcon />
              </IconButton>
              <img src="https://www.freepnglogos.com/uploads/email-logo-png-22.png"  alt="" />
          </div>
          <div className="header_middle "  >
           <SearchIcon />
           <input  input={input} 
       onChange={updateInput}/>
           <ArrowDropDownIcon className="header_inputCaret" />
              </div>  
           <div className="header_right ">
              <IconButton>
                  <AppsIcon />
              </IconButton>

              <IconButton >
                  <NotificationsIcon/>
              </IconButton>
              <Avatar src={user?.photoURL}/>
              <IconButton >    
                  <ExitToAppIcon onClick={handleLogout} />
              </IconButton>
          </div>

        </div>
    );
};

export default Header
