import React, { useState } from "react";
import './Header.css';
import MenuIcon from "@material-ui/icons/Menu";
import {Avatar, IconButton} from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import AppsIcon from "@material-ui/icons/Apps";
import NotificationsIcon from "@material-ui/icons/Notifications";
import {useDispatch, useSelector} from "react-redux";
import {selectUser} from "./features/userSlice";
import {logout} from "./features/userSlice";
import {auth} from "./Firebase";
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import {useEffect } from "react";


const  Header=({handleLogout}) => {

    const[value,setvalue]=useState('');
    const [result,setResult]=useState([]);
    useEffect( () =>{
        if(value.length>0){
            fetch('https://console.firebase.google.com/u/0/project/my-app-7fd52/firestore/data/~2Femails.json').then(

            response =>response.json() ).then(responseData =>{
                setResult([]);
                let searchQuery=value.toLowerCase();
                for(const key in responseData){
                    let emails=responseData[key].name.toLowerCase();
                    if(emails.slice(0,searchQuery.length).indexOf(searchQuery)!==-1){
                     setResult(prevResult => {
                         return[...prevResult,responseData[key].name]
                     });

                    }
                }
            }).catch(error => {
                console.log(error);
            })
            }else{
                setResult([]);
        }

    }, [value])
    const user = useSelector(selectUser);
    const dispatch = useDispatch();
 
    
    return (
        <div className="header">
          <div className="header_left">
              <IconButton>
              <MenuIcon />
              </IconButton>
              <img src="https://www.freepnglogos.com/uploads/email-logo-png-22.png"  alt="" />
          </div>
          <div className="header_middle "  >
           <SearchIcon />
           <input placeholder="search mail" type="text" id="latestHotDogStatus"
           
           onChange={(event) =>setvalue(event.target.value)}
           value={value}
           />
       <div className="searchback">

       { result.map((result,index) =>(
            <a href="#" key={index}>
                <div className="searchEntry">
                    {result}
                </div>
            </a>
           ))}
       </div>
           <ArrowDropDownIcon className="header_inputCaret" />
              </div>  
           <div className="header_right ">
              <IconButton>
                  <AppsIcon />
              </IconButton>

              <IconButton >
                  <NotificationsIcon/>
              </IconButton>
              <Avatar src={user?.photoURL}/>
              <IconButton >    
                  <ExitToAppIcon onClick={handleLogout} />
              </IconButton>
          </div>

        </div>
    );
};

export default Header
